<?php

require_once 'vendor/autoload.php';

use Dompdf\Dompdf;

// Read the path to the form data file from nameoftext.txt
// Read the path to the form data file from nameoftext.txt
// Read the path to the form data file from nameoftext.txt
$filePath = trim(file_get_contents('nameoftext.txt'));

// Read data from the specified file
$formData = file_get_contents($filePath);

// Initialize an array to store form data
$formDataArray = [];

// Split the form data into key-value pairs
foreach (explode("\n", $formData) as $line) {
    if (trim($line) === '') {
        continue;
    }

    if (strpos($line, ':') !== false) {
        list($key, $value) = explode(':', $line, 2);
        $formDataArray[trim($key)] = trim($value);
    } else {
        // Handle cases where a single key spans multiple lines
        // Append the value to the existing key
        $formDataArray[trim($key)] .= ' ' . trim($line);
    }
}

// Now $formDataArray should contain all the key-value pairs from the form data file



// Define an array of component names
$componentNames = [
    "Carrier Structure",
    "Tyre pressure",
    "Wheel Nuts",
    "Radiator water",
    "Engine Oil Level",
    "Diesel Level",
    "Diesel Tank",
    "Any Retrofitted Fuel Tanks",
    "Battery water level",
    "power take-off (PTO)",
    "propeller shaft",
    "hydraulic tank",
    "hydraulic pump",
    "hydraulic hose",
    "hydraulic oil",
    "slewing teble",
    "slewing gear and beaning",
    "elevating cylinder",
    "boom structure",
    "fly-jib",
    "single top",
    "sheave and hook block",
    "boom hoist wire",
    "load hoist wire",
    "winch motor",
    "clutch",
    "brake",
    "wire rope",
    "hook block",
    "operation control",
    "selector switch",
    "angle indicator",
    "pressure gauge",
    "LRl",
    "anti-tow block",
    "boom stop",
    "outrigger",
    "jack cylinder",
    "float"
];

// Extract component data from the form data
$componentDataStartIndex = strpos($formData, 'Carrier Structure:');
$componentData = substr($formData, $componentDataStartIndex);
$componentDataArray = explode("\n\n", $componentData);

// Initialize an array to store component data
$componentData = [];

// Iterate over each component data block
foreach ($componentDataArray as $block) {
    // Split the block into lines
    $lines = explode("\n", $block);

    // Extract component name
    $componentName = trim(array_shift($lines));

    // Initialize an array to store component fields
    $componentFields = [];

    // Iterate over each line to extract component fields
    foreach ($lines as $line) {
        if (strpos($line, ':') !== false) {
            list($key, $value) = explode(':', $line, 2);
            $componentFields[trim($key)] = trim($value);
        }
    }

    // Store component data
    $componentData[$componentName] = $componentFields;
}

// HTML content for the PDF
$html = '
