    .not-applicable .assessment-line {
      background-color: darkgrey;
      height: 45px;
    }

    .component-image {
      max-width: 200px; /* Max width for images */
      max-height: 200px; /* Max height for images */
      width: auto; /* Auto adjust width */
      height: auto; /* Auto adjust height */
    }
    
  </style>
</head>
<body>
  <div class="header">
    <img class="header-image" src="data:image/jpeg;base64," alt="Header Image">
    <p style=\'margin-left: 25%; margin-top: -8%;\'>Middle East Office Egypt:<br>
    221 Central Axis, 6 October City, Egypt<br>
    Tel: +201150778210<br>
    E-mail: info@isohere.com<br>
    www.infinitycert.com</p>
  </div>

  <table>';

// Add company information to HTML table
$html .= '
<tr><th colspan="2">Company Information</th></tr>
<tr><td>Company Name:</td><td>' . $formDataArray['Company Name'] . '</td></tr>
<tr><td>Subsidiary:</td><td>' . $formDataArray['Subsidiary'] . '</td></tr>
<tr><td>Department:</td><td>' . $formDataArray['Department'] . '</td></tr>
<tr><td>Street:</td><td>' . $formDataArray['Street'] . '</td></tr>
