<tr><td>Contact Person Phone:</td><td>' . $formDataArray['Contact Person Phone'] . '</td></tr>
<tr><td>Contact Person Fax:</td><td>' . $formDataArray['Contact Person Fax'] . '</td></tr>
<tr><td>Contact Person Email:</td><td>' . $formDataArray['Contact Person Email'] . '</td></tr>';

// Add crane information to HTML table
$html .= '
<tr><th colspan="2">Crane Information</th></tr>
<tr><td>Crane Type:</td><td>' . $formDataArray['Crane Type'] . '</td></tr>
<tr><td>Code / ID:</td><td>' . $formDataArray['Code / ID'] . '</td></tr>
<tr><td>Symbol:</td><td>' . $formDataArray['Symbol'] . '</td></tr>
<tr><td>Location / Number Plate:</td><td>' . $formDataArray['Location / Number Plate'] . '</td></tr>
<tr><td>Manufacturer:</td><td>' . $formDataArray['Manufacturer'] . '</td></tr>
<tr><td>Vehicle Type:</td><td>' . $formDataArray['Vehicle Type'] . '</td></tr>
<tr><td>Load Capacity [t]:</td><td>' . $formDataArray['Load Capacity [t]'] . '</td></tr>
<tr><td>Year of Build:</td><td>' . $formDataArray['Year of Build'] . '</td></tr>
<tr><td>Commissioning Date:</td><td>' . $formDataArray['Commissioning Date'] . '</td></tr>
<tr><td>Date of Last Inspection:</td><td>' . $formDataArray['Date of Last Inspection'] . '</td></tr>
<tr><td>Date of Next Inspection:</td><td>' . $formDataArray['Date of Next Inspection'] . '</td></tr> <br> <br>' ;

// Add component data to HTML table
foreach ($componentData as $componentName => $data) {
  // Determine the assessment status class based on data
  $statusClass = '';
  if (isset($data['Assessment'])) {
      switch (strtolower($data['Assessment'])) {
          case 'ok':
              $statusClass = 'ok';
              break;
          case 'to be fixed':
              $statusClass = 'to-be-fixed';
              break;
          case 'not ok':
              $statusClass = 'not-ok';
              break;
          case 'not applicable':
              $statusClass = 'not-applicable';
              break;
          default:
              $statusClass = '';
      }
  }

  // Add class to table row
  $html .= '<tr><th colspan="2">' . $componentName . '</th></tr>';
  foreach ($data as $field => $value) {
      if ($field === 'Assessment') {
          $html .= '<tr class="' . $statusClass . '"><td>' . $field . ':</td><td>' . $value . '<div class="assessment-line ' . $statusClass . '"></div></td></tr>';
      } elseif ($field === 'media' && !empty($value)) {
          // Add media section with image source if not empty
          $imageSrc = $value; // Assuming the value of media is the image source
          $html .= '<tr class="' . $statusClass . '"><td>' . $field . ':</td><td><img class="component-image" src="' . $imageSrc . '" alt="Media"></td></tr>';
      } elseif ($field !== 'media') {
          $html .= '<tr class="' . $statusClass . '"><td>' . $field . ':</td><td>' . $value . '</td></tr>';
      }
  }
}

$html .= '
  </table>
  <div class="footer">
    <img class="footer-image" src="data:image/jpeg;base64," alt="Footer Image">
    <!-- Add your footer content here -->
  </div>
</body>
</html>
';

// Create a new Dompdf instance
$dompdf = new Dompdf();

// Load HTML content into Dompdf
$dompdf->loadHtml($html);

// Set paper size and orientation
$dompdf->setPaper('A3', 'portrait');

// Render the HTML as PDF
$dompdf->render();

// Output the generated PDF to the browser (you can also save it to a file)
$dompdf->stream("crane_inspection_report.pdf", array("Attachment" => false));

?>
